package com.itheima_03;

public class ForTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 0; i < 5; i++) {
			System.out.println(i);
		}
		System.out.println("-------------");
		
		for (int i = 5; i > 0; i--) {
			System.out.println(i);
		}
	}

}
